custom_css:
.lilith-shrine {
background: radial-gradient(circle at center, var(--background-primary), #111);
padding: 2rem;
text-align: center;
}

.sacred-text {
font-family: serif;
color: var(--body-primary);
text-shadow: 0 0 5px crimson;
opacity: 0.8;
letter-spacing: 2px;
line-height: 1.8;
margin-bottom: 1.5rem;
}

.sigil-container {
font-size: 2.5em;
opacity: 0.6;
display: flex;
justify-content: center;
align-items: center;
}
.lilith-shrine::before {
content: "";
position: absolute;
top: 0;
left: 0;
width: 100%;
height: 100%;
background: url('https://files.shapes.inc/82d02dd3.png');
opacity: 0.15; /* Adjust as needed */
pointer-events: none; /* So it doesn't block clicks */
}
